  birthday, tel, grade, money, 
  status, ctime, mtime
  )
values (#{nickname,jdbcType=VARCHAR}, #{pwd,jdbcType=VARCHAR}, #{sex,jdbcType=TINYINT}, 
  #{birthday,jdbcType=DATE}, #{tel,jdbcType=BIGINT}, #{grade,jdbcType=TINYINT}, #{money,jdbcType=BIGINT}, 
  #{status,jdbcType=BIT}, #{ctime,jdbcType=TIMESTAMP}, #{mtime,jdbcType=TIMESTAMP}
  )
